mapper.insert:
INSERT INTO DEMO_COMPSERVERJOB_TB (
  COMPSERVERID, JOBNAME
) VALUES (
  #{compserverid}, #{jobname}
)

mapper.select:
SELECT workflowname, jobname, subtype, seq, pool, priority, expool FROM DEMO_WORKFLOWORDER_TB
AND WORKFLOWNAME = #{workflowname} AND SUBTYPE = #{subtype}
ORDER BY SEQ

SELECT A.*, (SELECT COUNT(*) FROM DEMO_COMPSERVER_TB WHERE STATUS > 0 AND CLIENTIP = A.CLIENTIP) AS WORKING FROM DEMO_COMPSERVER_TB A
POOL = #{pool} AND STATUS = 0 AND COMPSERVERID IN ( SELECT COMPSERVERID FROM DEMO_COMPSERVERJOB_TB
JOBNAME = #{jobname}
)
ORDER BY WORKING, TIMESTAMP LIMIT 1
